sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function(strDate) {
        this.rightnow = new GlideDateTime();
        this.testdate = new GlideDateTime(strDate);
    },

    isDateToday: function() {

        var currentDate = this.rightnow.getLocalDate();
        var testDate = this.testdate.getLocalDate();

        if (testDate.compareTo(currentDate) == 0) {
            return true;
        } else {
            return false;
        }

    },

    isDatePast: function() {
        // If the testdate is before rightnow, return true, else return false

        if (this.testdate.before(this.rightnow)) {
            return true;
        } else {
            return false;
        }
    },

    type: 'NeedItUtils'
};
